    if ((location.origin.lastIndexOf('/')) === location.origin.length + 1) {
        SiteUrl = location.origin.substring(location.origin.indexOf('/'), location.origin.lastIndexOf('/'));
    } else {
        SiteUrl = location.origin + "/";
    }    
